mapper.update:
update bill_record
set status = #{status}
where order_number = #{orderNumber} and account_shop_token=#{accountShopToken}

mapper.select:
select
sum(amount)
from bill_record
where account_shop_token = #{accountShopToken}
and status = #{type}

select

from bill_record
