$.validator.setDefaults({
    highlight: function (e) {
        $(e).closest(".form-group").removeClass("has-success").addClass("has-error")
    }, success: function (e) {
        e.closest(".form-group").removeClass("has-error").addClass("has-success")
    }, errorElement: "span", errorPlacement: function (e, r) {
        e.appendTo(r.is(":radio") || r.is(":checkbox") ? r.parent().parent().parent() : r.parent())
    }, errorClass: "help-block m-b-none", validClass: "help-block m-b-none"
}), $().ready(function () {
    $("#commentForm").validate();
    var e = "<i class='fa fa-times-circle'></i> ";
    $("#signupForm").validate({
        rules: {
            shopname: "required",
            shopAddress: "required",
            shopCategory: {required: !0, maxlength: 6},
            phone: {required: !0, maxlength: 11},
            email: {required: !0, email: !0},
            aliPay : "required",
            weChat : "required",
            shopMessage : {required: !0, maxlength:45},
        },
        messages: {
            shopname: e + "请输入店铺名字",
            shopAddress: e + "请输入店铺地址",
            shopCategory: {required: e + "请输入店铺类别", minlength: e + "店铺类别必须两个字符以上"},
            phone: {required: e + "请输入您的手机号", minlength: e + "手机号必须5个字符以上"},
            email: e + "请输入您的E-mail",
            aliPay : e + "请输入支付宝账号",
            weChat : e + "请输入微信账号",
            shopMessage : {required: e + "请输入店铺介绍", maxlength: e + "店铺介绍不能大于十五字符以上"},
        }
    }), $("#shopCategory").focus(function () {
        var e = $("#shopname").val(), r = $("#shopAddress").val();
        e && r && !this.value && (this.value = e + "." + r)
    })
});